import { useState, useEffect } from 'react';
import axios from 'axios';
import { TextField, InputAdornment } from '@mui/material';
import SearchIcon from '@mui/icons-material/Search';
import { trendingBaseUrl, searchBaseUrl } from '../../helpers/constants';
import PreviewContent from '../commons/PreviewContent';
import CustomPagination from '../commons/layouts/CustomPagination';
import { scrollToTop } from '../../helpers/functions'; 




function Search({ passoverPropObj }) {
    const { pageTitle, searchInputPlaceholder, mediaType, mediaTypeText, extraQueryStrForTrendingReq, extraQueryStrForSearchReq, extraQueryStrForDetailReq, loading,
      setLoading } = passoverPropObj;

    const [page, setPage] = useState(1);
    const [searchText, setSearchText] = useState('');
    const [content, setContent] = useState([]);
    const [numOfPages, setNumOfPages] = useState();
    const [timeoutId, setTimeoutId] = useState(null); 
    const apiCallManuallyTriggeredFlag = false; 

    const trendingUrlWtPage = trendingBaseUrl.replace('%mediaType%', mediaType)+extraQueryStrForTrendingReq;
    const searchUrlWtPageWtSearchText = searchBaseUrl.replace('%mediaType%', mediaType)+extraQueryStrForSearchReq;


    useEffect(() => {
      if(!apiCallManuallyTriggeredFlag) {}
      if(searchText.length === 0) {
        // console.log('1');
          // trending page request
        fetchTrendingContent();
      }else {
        // console.log('2');
        fetchDataForSearch();
      }

    }, [page]); // eslint-disable-line react-hooks/exhaustive-deps

    const fetchTrendingContent = async(paramPage = null) => {
      console.log('trending');
      setLoading(true);
      const reqPage = paramPage? paramPage : page;
      const trendingUrl = trendingUrlWtPage+`&page=${reqPage}`;
      const { data } = await axios.get(trendingUrl);

      setContent(data.results);
      setNumOfPages(data.total_pages);
      setLoading(false);
      scrollToTop();
    };


  const searchChanges = (e) => {

    const currentSearch = e.target.value.trim(); 
    const tempPrevSearch = searchText.trim();


    setSearchText(currentSearch);

    const minLenToTiggerApiCall = 1; 
    const timeIntervalForApiCall = 400; // ms 

    // canceling the previous reserved API call. 
    clearTimeout(timeoutId);
    setTimeoutId(null); // reset 

    // check if there is any changes in the search text
    if( currentSearch !== tempPrevSearch ) {
        if(currentSearch.length > minLenToTiggerApiCall) {
          // search request
          // for debouncing
          setTimeoutId(setTimeout(()=> { 

            // reset to page 1
            fetchDataForSearch(1, currentSearch);
          }, timeIntervalForApiCall));
        }else if(currentSearch.length === 0) {
            // empty search text -> reset to first trending page.
            fetchTrendingContent(1);
        }
    }
  }


  const fetchDataForSearch =  async (paramPage = null, paramSearchText = null) => {
    console.log('search');
    setLoading(true);
    const reqPage = paramPage? paramPage : page;
    const reqSearchText = paramSearchText? paramSearchText : searchText;
    const searchUrl = searchUrlWtPageWtSearchText+`&page=${reqPage}&query=${reqSearchText}`;
    const { data } = await axios.get(searchUrl);

    setContent(data.results);
    setNumOfPages(data.total_pages);

    setLoading(false);
    scrollToTop();
  }


    return (
        <section>
            <h2>{pageTitle}</h2> 
            <TextField className='mui-text-field' required label={searchInputPlaceholder} variant="standard"  InputProps={{
          startAdornment: (
            <InputAdornment position="start">
              <SearchIcon />
            </InputAdornment>
          ),
        }}  defaultValue={searchText} onChange={(e)=>searchChanges(e)} />        

      { (loading)? '' :   
          (<div>
          {
              content && content.map((previewInfo) => {
                   const passoverPropObj = {
                    id: previewInfo.id,
                    poster: previewInfo.poster_path, 
                    title: previewInfo.title || previewInfo.name,
                    date: previewInfo.release_date || previewInfo.first_air_date,
                    mediaType:mediaType,
                    mediaTypeText:mediaTypeText,
                    voteAvg:previewInfo.vote_average,
                    extraQueryStrForDetailReq,
                    setLoading
                   };
                  return <PreviewContent key={previewInfo.id} passoverPropObj={passoverPropObj} />;
              })
          }

          {
            !content && '0 results found'
          }
          {numOfPages > 1 && <CustomPagination currPage={page} setPage={setPage} numOfPages={numOfPages} />}
        </div>)
      }
        </section>
    )
}

export default Search
