import { Fragment } from 'react';
import LocalMoviesIcon from '@mui/icons-material/LocalMovies';

const styleHeader = {
    backgroundColor: 'rgb(0, 30, 60);',
    color: 'white',
    "& .MuiOutlinedInput-root:hover": { // one question: in this case is the first (yes) & refer to the one that styles2 is applied? then next & referes to the .MuiOutlinedInput-root?
        "& > fieldset" : {
            borderColor: 'orange'
        }
    }
}
const styleLogo = {
    color: 'blue',
    fontSize: '40px',
    display: 'flex',
    alignItems: 'center'
}



function Header() {
    return (
        <Fragment>
            <header sx={{...styleHeader}}>
                <h1><LocalMoviesIcon sx={{...styleLogo}} /> The Movies</h1>
            </header>
        </Fragment>
    )
}

export default Header
