import { useState } from 'react';
import axios from 'axios';
import { Divider, Stack, styled, Paper } from '@mui/material';
import TransitionModal from './layouts/TransitionModal';
import { detailsBaseUrl, imgUrlTemplateForW500, posterReplacementImgForImgSize500 } from '../../helpers/constants';

const Item = styled(Paper)(({ theme }) => ({
    ...theme.typography.body2,
    padding: theme.spacing(1),
    textAlign: 'center',
    color: theme.palette.text.secondary,
  }));

const DetailContent = ({ id, mediaType, extraQueryStrForDetailReq, setLoading }) => {
    const detailUrl = detailsBaseUrl.replace('%mediaType%', mediaType).replace('%id%', id)+extraQueryStrForDetailReq;

    const [content, setContent] = useState(null);

    const fetchContentInDetail = async (modalOpenFunc) => {
        setLoading(true);

        const { data } = await axios.get(detailUrl).then(() => {
            setContent(data);
            setLoading(false);

            modalOpenFunc();
        }).catch(() => {
            alert('Sorry, some problem has occurred. Please try it again later.');
        });
    };


    return (
        <TransitionModal fetchContentInDetail={fetchContentInDetail}>
            {content}
           <Stack direction='row' divider={<Divider orientation="vertical" flexItem />} spacing={0}>
                <Item><img src={content?.poster_path? `${imgUrlTemplateForW500}/${content.poster_path}` :  posterReplacementImgForImgSize500} alt={content.name || content.title} /></Item>
                <Item>
                    <p>{content.name || content.title}</p>
                    <p>{content.first_air_date || content.release_date}</p>
                    <p>{content.tagline}</p>
                    <p>{content.overview}</p>
                </Item>
                {console.log(content)}
            </Stack>
        </TransitionModal>
    )
}

export default DetailContent
