import { useState } from "react";
import axios from "axios";
import { Divider, Stack, styled, Paper } from "@mui/material";
import {
    detailsBaseUrl,
    imgUrlTemplateForW500,
    posterReplacementImgForImgSize500,
  } from "../../helpers/constants";
import TransitionModal from "./layouts/TransitionModal";

const Item = styled(Paper)(({ theme }) => ({
  ...theme.typography.body2,
  padding: theme.spacing(1),
  textAlign: "center",
  color: theme.palette.text.secondary,
}));

const DetailContent = ({
  id,
  mediaType,
  extraQueryStrForDetailReq,
  setLoading,
}) => {
  const detailUrl =
    detailsBaseUrl.replace("%mediaType%", mediaType).replace("%id%", id) +
    extraQueryStrForDetailReq;

  const [content, setContent] = useState(null);

  const fetchContentInDetail = async (modalOpenFunc) => {
    setLoading(true);
    try {
        const data  = await axios
        .get(detailUrl);

        setContent(data);
        setLoading(false);

       // modalOpenFunc();
    }catch(err) {
        alert("Sorry, some problem has occurred. Please try it again later.");
    }
  };

  return (
    <TransitionModal fetchContentInDetail={fetchContentInDetail}>

    </TransitionModal>
  );
};

export default DetailContent;
